import React, { useState, useEffect, useContext } from "react";
import { StyleSheet, Text, View, Button, TextInput, ActivityIndicator, AsyncStorage } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { AuthContext } from './AuthProvider';
import { Authentication } from './Authentication';
import { Dash_Res } from './Dash_Res';
import { Dash_Admin } from "./Dash_Admin";
import { Dash_Tech } from "./Dash_Technician";
import * as SecureStore from 'expo-secure-store';

export default function Routes() {
  const { user, setUser, login, logout } = useContext(AuthContext)
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // check if the user is logged in or not
    SecureStore.getItemAsync('user')
      .then(userString => {
        if (userString) {
          // decode it
          login();
          userObject = JSON.parse(userString)
          setUser(userObject);
        }
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
        setLoading(false)
      })
  }, []);

  if (loading) {
    return (
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <ActivityIndicator size="large" />
      </View>
    )
  }
  return (
    <NavigationContainer>
      {user ? 
          ((user.id == 2) ? <Dash_Admin />
                      : (user.id==1 ? <Dash_Res/>
                                    : <Dash_Tech />) )
        : <Authentication />}
    </NavigationContainer>
  );
}
