import React, { useState, useEffect } from "react";
import { TouchableOpacity, Text,   StatusBar,  View, Image, StyleSheet,FlatList,  SafeAreaView } from "react-native";
import axios from 'axios';

axios.defaults.baseURL = 'http://127.0.0.1:8000';


function Services({ navigation }) {
  const [dataa, setdataa] = useState([]);
  useEffect(() => {
    axios.get('/api/home_data')
      .then(response => {
        if(response && response.data){
           setdataa(response.data);
           console.log('congrats, data fetch successfully');}})
      .catch(error => {
        console.log("hooooo errror aaa", error.response);
      })
  }, []);
  
  const Item = ({std1, title, id, price, image, colors}) => (
  <TouchableOpacity onPress={() => {navigation.navigate('Wishlist', {std1: std1})}}>
    <View style={styles.item}>
      <Image source={{ uri: `http://127.0.0.1:8000/storage/${image}`, }} style={styles.image} />
      <Text style={styles.title}>Name, id={id} </Text>
      <Text style={styles.value}>{title}</Text>
      <Text style={styles.title}>Charges: </Text> 
      <Text style={styles.value}>{price}</Text>
      <Text style={styles.title}>Timing</Text>
      {colors && colors.map(x => <Text style={styles.value}> {x.code} to {x.code1}</Text>)}
    </View>
  </TouchableOpacity>
  );
  
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <SafeAreaView style={styles.container}>
        <FlatList
          numColumns={2}
          data={dataa}
          renderItem={({item}) => <Item std1={item} id={item.id} title={item.title} price={ item.price } image={item.image} colors={item.colors} />}
          keyExtractor={item => item.id}
        />
      </SafeAreaView>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {

    backgroundColor: '#f9c27f',
    padding: 14,
    marginVertical: 8,
    marginHorizontal: 6,
    minWidth: 164,
    maxWidth: '99%',

    minHeight: 360,
    borderRadius:20,
  },
  image:{
    width: 160,
    height: 95,
    borderRadius: 20,

   },
  title: {
    color: 'blue',
    fontSize: 26,
    textAlign: 'center',
  },
  value: {
    fontSize: 18,
    maxWidth: 130,
    color:'purple',
    textAlign: 'center',

  },
});


export default Services;
