import React, { useContext, useState, useEffect } from "react";
import { createStackNavigator } from "@react-navigation/stack";
import { AuthContext } from "../../AuthProvider";
import { Button, Text, View, Image, StyleSheet } from "react-native";
import axios from 'axios';
axios.defaults.baseURL = 'http://127.0.0.1:8000';
const Stack = createStackNavigator();

function DashboardScreen({ navigation }) {
  const { user, logout } = useContext(AuthContext)
  const [name, setName] = useState(null);

  useEffect(() => {
    axios.defaults.headers.common['Authorization'] = `Bearer ${user.token}`;
    axios.get('/api/home_data')
      .then(response => {
        // console.log("\n\n\n\n\n****************** response ***********************************************\n\n\n ",response);
        // console.log("\n\n\n\n\n^^^^^^^^^^^^^^^^^^ rsponse.data **********************\n\n\n ",response.data);
        // console.log("\n\n\n\n\n^^^^^^^^^^^^^^^^^^ rsponse.data.Array **********************\n\n\n ",response.data[2]);
        var lst = [];
        const a = 'http://127.0.0.1:8000/storage/';
        for (let i = 0; i < response.data.length; i++) {
          const obj = response.data[i].image;
          const fnl = `${a}${obj}`;
          lst.push(fnl);
          }
        console.log(lst);
  
      })
      .catch(error => {
        console.log("hooooo aaa", error.response);
      })

  }, []);
  const onPressCategory = (item) => {
    const title = item.name;
    const category = item;
    navigation.navigate("RecipesList", { category, title });
  };

  const renderCategory = ({ item }) => (
    <TouchableHighlight underlayColor="rgba(73,182,77,0.9)" onPress={() => onPressCategory(item)}>
      <View style={styles.categoriesItemContainer}>
        <Image style={styles.categoriesPhoto} source={{ uri: item.photo_url }} />
        <Text style={styles.categoriesName}>{item.name}</Text>
        <Text style={styles.categoriesInfo}>{getNumberOfRecipes(item.id)} recipes</Text>
      </View>
    </TouchableHighlight>
  );

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Admmmmmin </Text>
      <View>
      <View>
      <FlatList data={categories} renderItem={renderCategory} keyExtractor={(item) => `${item.id}`} />
    </View>
      </View>
      {/* <Text>User   :    {user.email}</Text>
      <Text>products.........   :   {name}</Text>
      <Text>User role   :   {user.role}</Text>
      <Text>User id   :   {user.id}</Text>
      <Button title="Go to Settings" onPress={() => navigation.navigate('Settings')} />
      <Button title="Logooout" onPress={() => logout()} /> */}
    </View>
  );
}

function SettingsScreen({ navigation }) {
  const { user, logout } = useContext(AuthContext)

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Settings Screen</Text>
      <Text>User: {user.email}</Text>
      <Button title="Go to Dashboard" onPress={() => navigation.navigate('Dashboard Admin')} />
      <Button title="Logout" onPress={() => logout()} />
    </View>
  );
}

const Products = () => {
  return (
    <Stack.Navigator>
      <Stack.Screen name="Dashboard Admin" component={DashboardScreen}  options={{
          headerStyle: {
            backgroundColor: '#f4511e',},
          headerTintColor: '#fff',
          paddingleft:120,
          headerTitleStyle: {
            fontWeight: 'bold',
          },
        }}/>
      <Stack.Screen name="Settings" component={SettingsScreen} />
    </Stack.Navigator>
  )
};

export default Products;
