import React, { useContext, useState, useEffect } from "react";
import { Pressable, TouchableOpacity, Text,   StatusBar,  View, Image, StyleSheet,FlatList, TouchableHighlight,   SafeAreaView } from "react-native";
import axios from 'axios';

axios.defaults.baseURL = 'http://127.0.0.1:8000';

function WishlistScreen({route, navigation, std1}) {
  const { itemId } = route.params;

  const [id, setId] = useState(itemId.id);

 const  wishadd =((id) => {

    axios.post('/api/wishlist_add')
    .then(response => {
      if (response){
        console.log("\n*********wishlist has been added, sir ggg *********\n ", response);

      }
      console.log("\n*********wishlist has been added, sir ggg *********\n ", response);
    })
    .catch(error => {
        console.log('ooooyyeee error aa gaya', error);
      
    })
  });

  return (
    <View style={styles.item}>
      <Image source={{ uri: `http://127.0.0.1:8000/storage/${itemId.image}`, }} style={styles.image} />
      {/* <Image source={{ uri: '${itemId.image}', }} style={styles.image} /> */}
      <Text style={styles.title}>Name</Text>
      <Text style={styles.value}>{itemId.title}</Text>
      <Text style={styles.title}>Category</Text>
      <Text style={styles.value}>{itemId.category.name}</Text>
      <Text style={styles.title}>Charges(min): </Text> 
      <Text style={styles.value}>{itemId.price}</Text>
      <Text style={styles.title}>Timing</Text>
      {itemId.colors && itemId.colors.map(x => <Text style={styles.value}> {x.code} to {x.code1}</Text>)}
      <Text style={styles.title}>Description</Text>
      <Text style={styles.value}>{itemId.description}</Text>
  
      <Pressable
              style={[styles.button, styles.buttonClose]}
              onPress={() =>wishadd(id) }>
              <Text style={styles.textStyle}>add to wishlist</Text>
            </Pressable>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {

    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  buttonOpen: {
    backgroundColor: '#F194FF',
  },
  buttonClose: {
    backgroundColor: '#2196F3',
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  item: {
    flex: 1, 
    alignItems: 'center',
    justifyContent: 'center' ,
    backgroundColor: '#f9c29f',
    padding: 14,
    marginVertical: 8,
    // marginHorizontal: 'auto',
    width:350,

  },
  image:{
    width: 260,
    height: 250,
    borderRadius: 20,
    justifyContent: 'center',
    alignContent: 'center',
    textAlign: 'center',

   },
  title: {
    color: 'red',
    fontSize: 20,
    textAlign: 'center',
  },
  value: {
    fontSize: 18,
    maxWidth: 330,
    color:'purple',
    textAlign: 'center',

  },
});
export default WishlistScreen;
